import logo from "./logo.svg";
import "./App.css";
import { useEffect, useRef, useState } from "react";

function App() {
  const [time, setTime] = useState(0);
  // setInterval(() => {
  //   setTime(time + 1);
  //   console.log(time);
  // }, 1000);

  useEffect(() => {
    handleTime();
    return () => clearInterval(id.current);
  }, []);
  let id = useRef();

  function handleTime() {
    id.current = setInterval(() => {
      setTime((prev) => prev + 1);
      console.log(time);
    }, 1000);
  }

  return (
    <div className="App">
      <h1>Stop Watch</h1>
      <h1>{time}</h1>
      <button>Start</button>
      <button>Pause</button>
      <button>Stop</button>
    </div>
  );
}

export default App;
